/* Create Tables */
CREATE TABLE SYS_USER_TO_PRIVILEGE
(
	SYS_USER_ID varchar2(50) NOT NULL,
	SYS_PRIVILEGE_ID varchar2(50) NOT NULL,
	PRIMARY KEY (SYS_USER_ID, SYS_PRIVILEGE_ID)
);

CREATE TABLE SYS_ROLE_TO_PRIVILEGE
(
	SYS_ROLE_ID varchar2(50) NOT NULL,
	SYS_PRIVILEGE_ID varchar2(50) NOT NULL,
	PRIMARY KEY (SYS_ROLE_ID, SYS_PRIVILEGE_ID)
);

CREATE TABLE SYS_PRIVILEGE
(
	SYS_PRIVILEGE_ID varchar2(50) NOT NULL,
	SYS_MENU_ID varchar2(50) NOT NULL,
	PRIVILEGE_CODE varchar2(100),
	PRIVILEGE_NAME varchar2(100),
	DESCRIPTION varchar2(500),
	USERS_COUNT number,
	ROLES_COUNT number,
	ALL_USERS_COUNT number,
	DISPLAY_ORDER number,
	PRIMARY KEY (SYS_PRIVILEGE_ID),
	CONSTRAINT UK_PRIVILEGE_NAME UNIQUE (PRIVILEGE_NAME)
);

CREATE TABLE SYS_USER_TO_ROLE
(
	SYS_USER_ID varchar2(50) NOT NULL,
	SYS_ROLE_ID varchar2(50) NOT NULL,
	PRIMARY KEY (SYS_USER_ID, SYS_ROLE_ID)
);

CREATE TABLE SYS_ROLE
(
	SYS_ROLE_ID varchar2(50) NOT NULL,
	ROLE_NAME varchar2(100) NOT NULL,
	DESCRIPTION varchar2(500),
	USERS_COUNT number,
	PRIVILEGES_COUNT number,
	DEPARTMENTS_COUNT number,
	CREATE_BY varchar2(100) NOT NULL,
	CREATE_DATE timestamp(0) NOT NULL,
	UPDATE_BY varchar2(100),
	UPDATE_DATE timestamp(0),
	PRIMARY KEY (SYS_ROLE_ID),
	CONSTRAINT UK_ROLE_NAME UNIQUE (ROLE_NAME)
);

CREATE TABLE SYS_DEPARTMENT
(
	SYS_DEPARTMENT_ID varchar2(50) NOT NULL,
	CODE varchar2(30),
	DEPARTMENT_NAME varchar2(100),
	PARENT_ID varchar2(50),
	DESCRIPTION varchar2(500),
	ADMIN_USER_ID varchar2(50),
	CREATE_BY varchar2(100) NOT NULL,
	CREATE_DATE timestamp(0) NOT NULL,
	UPDATE_BY varchar2(100),
	UPDATE_DATE timestamp(0),
	STATUS varchar2(2),
	PRIMARY KEY (SYS_DEPARTMENT_ID)
);

CREATE TABLE SYS_MENU
(
	SYS_MENU_ID varchar2(50) NOT NULL,
	PARENT_ID varchar2(50) NOT NULL,
	MENU_NAME varchar2(100) NOT NULL,
	MENU_URL varchar2(400),
	MENU_ICON varchar2(100),
	DISPLAY_ORDER number,
	PRIMARY KEY (SYS_MENU_ID)
);

CREATE TABLE SYS_MESSAGE (
  ID varchar(50) NOT NULL,
  TITLE varchar(250) DEFAULT NULL,
  CONTENT varchar(4000) DEFAULT NULL,
  SEND_DATE date DEFAULT NULL,
  SENDER_ID varchar(50) DEFAULT NULL,
  RECEIVER_ID varchar(50) DEFAULT NULL,
  STATE varchar(10) DEFAULT NULL,
  INSTANCE_ID varchar(50) DEFAULT NULL,
  PRIMARY KEY (ID)
);

CREATE TABLE SYS_MODULE_DEFINITION (
  ID varchar(50) NOT NULL,
  NAME varchar(50) DEFAULT NULL,
  DESCRIPTION varchar(500) DEFAULT NULL,
  DISPLAY_ORDER number,
  MENU_ID varchar(50) DEFAULT NULL,
  PRIMARY KEY (ID)
);

CREATE TABLE SYS_USER
(
	SYS_USER_ID varchar2(50) NOT NULL,
	USERNAME varchar2(100) NOT NULL,
	PASSWORD varchar2(100) NOT NULL,
	ENABLED char NOT NULL,
	STATE varchar2(4) NOT NULL,
	ROLES_COUNT number,
	PRIVILEGES_COUNT number,
	CREATE_BY varchar2(100) NOT NULL,
	CREATE_DATE timestamp(0) NOT NULL,
	UPDATE_BY varchar2(100),
	UPDATE_DATE timestamp(0),
	SYS_DEPARTMENT_ID varchar2(50),
	GENDER char NOT NULL,
	REAL_NAME varchar2(50),
	ID_CARD varchar2(20),
	ADDRESS varchar2(250),
	EMAIL varchar2(100),
	PHONE_NUMBER varchar2(100),
	IS_DEPARTMENT_ADMIN varchar2(4) DEFAULT '0',
	type                VARCHAR2(4) default '3',
	PRIMARY KEY (SYS_USER_ID)
);

CREATE TABLE SYS_DATA_PERMISSION_FILTER
(
	ID varchar2(50) NOT NULL,
	SYS_MENU_ID varchar2(50) NOT NULL,
	SYS_MENU_NAME varchar2(100) NOT NULL,
	DATA_FILTER varchar2(2000) DEFAULT NULL,
	DESCRIPTION varchar2(500) DEFAULT NULL,
	PRIMARY KEY (ID)
);

CREATE TABLE SYS_DICTIONARY_GROUP (
  ID varchar(50) NOT NULL,
  GROUP_KEY varchar(50) DEFAULT NULL,
  GROUP_NAME varchar(50) DEFAULT NULL,
  DESCRIPTION varchar(1000) DEFAULT NULL,
  PRIMARY KEY (ID)
);

CREATE TABLE SYS_DICTIONARY (
  ID varchar(50) NOT NULL,
  GROUP_ID varchar(50) DEFAULT NULL,
  DICT_KEY varchar(50) DEFAULT NULL,
  DICT_VALUE varchar(50) DEFAULT NULL,
  DICT_ORDER int DEFAULT '0',
  PRIMARY KEY (ID)
);

CREATE TABLE SYS_LOG_LOG
(
   SYS_LOG_LOG_ID VARCHAR2(50 BYTE) NOT NULL,
	ACCESS_URL VARCHAR2(500 BYTE) NOT NULL,
	ACCESS_NAME VARCHAR2(500 BYTE),
	LOG_DATE TIMESTAMP (0) NOT NULL,
	USERNAME VARCHAR2(100 BYTE),
	IP VARCHAR2(20 BYTE) NOT NULL,
	LOG_TYPE char(1) DEFAULT '1',
	PRIMARY KEY (SYS_LOG_LOG_ID)
);

CREATE TABLE SYS_LOG_PARAM
(
   SYS_LOG_PARAM_ID VARCHAR2(50 BYTE) NOT NULL,
	PARAM_NAME VARCHAR2(100 BYTE),
	PARAM_VALUE VARCHAR2(100 BYTE),
	SYS_LOG_LOG_ID VARCHAR2(50 BYTE) NOT NULL,
	PRIMARY KEY (SYS_LOG_PARAM_ID)
);

CREATE TABLE EXCEPTION_LOG
(
  LOG_ID     VARCHAR2(50) NOT NULL,
